import React, { useState, useEffect } from 'react';
import { Chart, Line } from 'react-chartjs-2';
import {
    Chart as ChartJS,
    LineElement,
    CategoryScale,
    LinearScale,
    PointElement
} from 'chart.js';

ChartJS.register(
    LineElement,
    CategoryScale,
    LinearScale,
    PointElement
)

ChartJS.defaults.color = "#22c55e";

export default function ViewProfile({ id }){
    const [user, setUser] = useState({})
    const [isLoading, setIsLoading] = useState(false)
    const [data, setData] = useState({
        labels: ["","","","","","","","","","last"],
        datasets: [{
            data: [],
            backgroundColor: "transparent",
            borderColor:  "#047857",
            pointBorderColor: "transparent",
            pointBorderWidth: 4,
            scaleFontColor: "#047857",
        }],
    })

    const options = {
        plugins: {
            legend: false
        },
        scales: {
            x: {
                grid: {
                    display: false,
                }
            },
            y: {
                ticks: {
                    stepSize: 20,
                }
            }
        }
    }

    useEffect(() => {
        setIsLoading(true)
        fetch(`http://localhost:3000/scores/user/${2}`, {
        })
        .then(r => r.json())
        .then(user => {
            setUser()
            data.datasets[0].data = data.scores.map((value) => {
                return value.score;
            }).slice(-10)
            setIsLoading(false)
        })
    }, [])

    return(
        <div className="relative w-2/5 mt-14 z-20 rounded-xl justify-center mx-auto flex flex-col h-fit bg-sky-900">
            <div className="flex flex-row items-center mx-auto relative mt-6 mb-6">
                <img className="h-32 w-32" src={user.avatar_url ? user.avatar_url : "https://cdn-icons-png.flaticon.com/512/2458/2458293.png"}/>
                <div className="h-fit mx-auto">
                    <div className="ml-4 flex flex-col h-full w-full">
                        <h3 className="pt-3 text-emerald-500">{user.email} <span className="text-xs">place of origin</span></h3>
                        <p className="text-emerald-500 mt-2">high_score: {user.high_score}</p>
                    </div>
                </div>
            </div>
            <div className="">
                {isLoading ? null : <Line className="p-10" data={data} options={options} />}
            </div>
        </div>
    )
}
