import React, { useEffect, useState } from "react";
import Keyboard from "./Keyboard";

export default function Test(){
    const [wordsArray, setWordsArray] = useState([])
    const words = ["yes", "yes", "yes", "yes", "yes", "yes", "yes", "yes", "yes", "yes", "yes", "yes", "yes", "yes", "yes", "yes", "yes", "no", "no", "no", "no", "no", "no", "no", "no", "no", "no", "no", "no", "no", "no", "no", "no", "no", "no", "no", "no", "no"]
    const textArea = document.querySelector('#text-area');
    const [blur, setBlur] = useState(true)
    const [inputValue, setInputValue] = useState("")

    console.log(inputValue)

    useEffect(() => {
        setWordsArray(words)
    }, [])

    function iterate(word){
        let letters = []
        for (let i = 0; i < word.length; i++) {
            letters.push(<letter className="text-2xl font-mono">{word[i]}</letter>)
        }
        return letters;
    }

    function createWords(words){
        return(
            <div className="pl-2 h-8">
                {iterate(words)}
            </div>
        )
    }


    window.addEventListener('keydown', e => {

    })

    window.addEventListener('keyup', e => {
        let focus = document.querySelector("#typeInput")
        setBlur(false)
        focus.focus()
        // console.log(e.key)
    })

    const handleOnFocus = () => {
        let blurZone = document.querySelector("#test-zone")
        blurZone.classList.remove('blur-sm')
    }

    const handleOnBlur = () => {
        let blurZone = document.querySelector("#test-zone")
        blurZone.classList.add('blur-sm')
    }

    function handleBlurClick(){
        let focus = document.querySelector("#typeInput")
        focus.focus()
        document.querySelector("#test-zone").classList.remove('blur-sm')
    }

    return (
        <div className="w-1/3 h-32 ml-2 pt-3">
            <div id="test-zone" className={`overflow-hidden h-24 bg-emerald-800 rounded-lg ${blur ? 'blur-sm' : null}`} onClick={() => handleBlurClick}>
                <input id="typeInput" autoFocus className="absolute z-0" onFocus={handleOnFocus} onBlur={handleOnBlur} value={inputValue} onChange={(e) => setInputValue(e.target.value)}/> 
                <p>click</p>
                <div id="text-area" className="flex flex-wrap z-10">{wordsArray.map((word) => {
                    return createWords(word)
                })}
                </div>
            </div>
            <Keyboard />
        </div>
    )
}
