import React, { useEffect, useState } from 'react';
import { ScrollRestoration, useNavigate } from 'react-router-dom';
import { Chart, Line } from 'react-chartjs-2';
import {
    Chart as ChartJS,
    LineElement,
    CategoryScale,
    LinearScale,
    PointElement
} from 'chart.js';

ChartJS.register(
    LineElement,
    CategoryScale,
    LinearScale,
    PointElement
)

ChartJS.defaults.color = "#22c55e";

export default function ProfileExists({ current_user }){
    const navigate = useNavigate();
    const [isLoading, setIsLoading] = useState(false)
    const [highScore, setHighScore] = useState(0)
    const [avatar, setAvatar] = useState({
        file:null
    })
    const [image, setImage] = useState("")

    const [data, setData] = useState({
        labels: ["","","","","","","","","","last"],
        datasets: [{
            data: [],
            backgroundColor: "transparent",
            borderColor:  "#047857",
            pointBorderColor: "transparent",
            pointBorderWidth: 4,
            scaleFontColor: "#047857",
        }],
    })

    const options = {
        plugins: {
            legend: false
        },
        scales: {
            x: {
                grid: {
                    display: false,
                }
            },
            y: {
                ticks: {
                    stepSize: 20,
                    callback: (value) => (value) + "WPM"
                }
            }
        }
    }

    useEffect(() => {
        if(avatar.file !== null){
            const imageUrl = URL.createObjectURL(avatar)
            setImage(imageUrl)
        }
    }, [avatar])

    useEffect(() => {
        setIsLoading(true)
        fetch(`http://localhost:3000/users/scores/${current_user.id}`, {
        headers: {
            Authorization: localStorage.getItem("token")
        }
        })
        .then(r => r.json())
        .then(scores => {
            let scoresArray = [];
            let dataScores = scores;
            scores.forEach((score) => {
                scoresArray.push(parseFloat(score.score));
            })
            data.datasets[0].data = dataScores.map((value) => {
                return value.score;
            }).slice(-10)
            setHighScore(Math.max(...scoresArray))
            setIsLoading(false)
        })
    }, [])

    function handleAvatarSubmit(e){
        e.preventDefault();
        if (avatar !== null){
            const form = new FormData();
            form.append("avatar", avatar)
            fetch("http://localhost:3000/avatar", {
            method: "post",
            headers: {
                Authorization: localStorage.getItem("token"),
            },
            body: form,
            })
            .then((res) => {
                if (res.ok) {
                console.log(res);
                return res.json();
                } else {
                throw new Error(res);
                }
            })
        } else {
            alert("no file has been attached")
        }
    }

    console.log(data.labels)

    console.log(data.datasets[0].data)

    return (
        <div className="relative w-2/5 mt-14 z-20 rounded-xl justify-center mx-auto flex flex-col h-fit bg-sky-900">
            <div className="flex flex-row items-center justify-center relative mt-6 mb-6">
                <img className="h-32 w-32 object-cover border-4 border-emerald-500" src={current_user.avatar_url ? current_user.avatar_url : "https://cdn-icons-png.flaticon.com/512/2458/2458293.png"}/>

                    <div className="ml-4 flex flex-col h-full w-1/3">
                        <h3 className="text-emerald-500">{current_user.username} <span className="text-xs">place of origin</span></h3>
                        <p className="text-emerald-500">Highscore: {highScore}WPM</p>
                        <div className="relative w-full">
                                {avatar.file ? <button className="" onClick={(e) => handleAvatarSubmit(e)}>submit</button> : 
                                    <>
                                        <input onChange={(e) => setAvatar(e.target.files[0])} id="file" placeholder="yes" type="file" name="avatar" className="w-full hidden"  />
                                        <label className="text-md text-ek-500 bg-emerald-500 rounded-md" for="file">change your avatar</label>
                                    </>
                                }
                        </div>
                        <button onClick={() => navigate('/profile/edit')} className="bg-emerald-500 text-sky-900 rounded-md w-1/2 hover:bg-emerald-300">settings</button>
                    </div>
            </div>
            <h2 className="text-center text-lg underline underline-offset-4 italic text-emerald-500">previous ten rounds</h2>
            <div className="">
                {isLoading ? null : <Line className="p-10" data={data} options={options} />}
            </div>
        </div>
    )
}
