import React, { useEffect, useState } from "react";
import Keyboard from "./Keyboard";

export default function Test(){
    const [wordsArray, setWordsArray] = useState([])
    const words = ["yes", "yes", "yes", "yes", "yes", "yes", "yes", "yes", "yes", "yes", "yes", "yes", "yes", "yes", "yes", "yes", "yes", "no", "no", "no", "no", "no", "no", "no", "no", "no", "no", "no", "no", "no", "no", "no", "no", "no", "no", "no", "no", "no"]
    const textArea = document.querySelector('#text-area');

    useEffect(() => {
        setWordsArray(words)
    }, [])

    function iterate(word){
        let letters = []
        for (let i = 0; i < word.length; i++) {
            letters.push(<letter className="text-2xl font-mono">{word[i]}</letter>)
        }
        return letters;
    }

    function createWords(words){
        return(
            <div className="pl-2 h-8">
                {iterate(words)}
            </div>
        )
    }


    window.addEventListener('keydown', e => {

        console.log(e.key)
    })

    window.addEventListener

    function handleFocusout(){
        textArea.classList.add('blur-sm')
    }

    // function handleBlur(e){
    //     e.target.classList.add('blur-sm')
    // }

    return (
        <div className="w-1/3 h-32 ml-2 pt-3">
            <div className="overflow-hidden h-24 bg-emerald-800  rounded-lg">
                <input id="typeInput" autoFocus className="absolute hidden" onBlur={() => handleFocusout}/> 
                <div id="text-area" className="flex flex-wrap">{wordsArray.map((word) => {
                    return createWords(word)
                })}
                </div>
            </div>
            <Keyboard />
        </div>
    )
}
