import React, { useEffect, useState } from "react";
import Keyboard from "./Keyboard";

export default function Test(){
    const [wordsArray, setWordsArray] = useState([])
    const words = ["yes", "yes", "yes", "yes", "yes", "yes", "yes", "yes", "yes", "yes", "yes", "yes", "yes", "yes", "yes", "yes", "yes"]

    useEffect(() => {
        setWordsArray(words)
    }, [])

    function iterate(word){
        let letters = []
        for (let i = 0; i < word.length; i++) {
            letters.push(<letter className="text-2xl font-mono">{word[i]}</letter>)
        }
        return letters;
    }

    function createWords(words){
        return(
            <div className="pl-2 h-8">
                {iterate(words)}
            </div>
        )
    }

    return (
        <div className="m-0">
        <div className=" flex flex-wrap bg-emerald-800 overflow-hidden rounded-lg pt-3">{wordsArray.map((word) => {
            return createWords(word)
        })}
        </div>
        <Keyboard />
        </div>
    )
}
