import React, { useEffect, useState } from 'react';
import { Form } from 'react-router-dom';

export default function ProfileEdit({ current_user }){
    const [origin, setOrigin] = useState(false)
    const [username, setUsername] = useState("")
    const [viewScore, setViewScore] = useState(false)
    const [viewProfile, setViewProfile] = useState(false)

    function onUsernameSubmit(e){
        e.preventDefault()
        fetch(`http://localhost:3000/current_user/username`,{
          method:'PATCH',
          headers: {
            Authorization: localStorage.getItem("token"),
            'Content-Type': 'application/json',
        },
          body:JSON.stringify({
            usermame: username,
          })
        })
        .then(res => {
          if(res.ok){
            console.log("success", res)
          } else {
            console.log("failure", res)
          }
        })
        .then((r) => r.json())
        .then((data) => {
            console.log(data)
        })

      }

    return (
        <div className="relative w-1/4 mt-14 z-20 rounded-xl mx-auto flex flex-col h-fit bg-sky-900">
            <div className="flex flex-col justify-center relative m-6">
                <img className="h-32 w-32 mx-auto object-cover border-4 border-emerald-500 rounded-md" src={current_user.avatar_url ? current_user.avatar_url : "https://cdn-icons-png.flaticon.com/512/2458/2458293.png"}/>
            </div>
            <h2 className="text-center text-xl italic text-emerald-500">Preferences:</h2>
            <div className="w-1/2 mx-auto">
                <div className="dropdown dropdown-right h-10 w-full flex items-center justify-center">
                    <input placeholder="(5+ characters)" className="bg-emerald-500 rounded-md w-full py-1 placeholder-sky-900/70 animate-pulse text-sky-900 font-bold text-sm text-center"/>
                </div>
            </div>
            <div className="w-1/2 mx-auto">
                <div className="dropdown dropdown-right my-3 w-full">
                    <label tabIndex={0} className="btn no-animation m-1 bg-emerald-500 text-sky-900 hover:bg-emerald-300 w-full">profile: private</label>
                    <ul tabIndex={0} className="dropdown-content menu p-2 shadow rounded-box w-52 bg-sky-800">
                        <li className="bg-emerald-500 hover:bg-emerald-300 border-b-[4px] border-sky-800 text-sky-900"><a>private</a></li>
                        <li className="bg-emerald-500 hover:bg-emerald-300 text-sky-900"><a>public</a></li>
                    </ul>
                </div>
            </div>
            <div className="w-1/2 mx-auto">
                <div className="dropdown dropdown-right my-3">
                    <label tabIndex={0} className="btn no-animation m-1 bg-emerald-500 text-sky-900 hover:bg-emerald-300 w-full">highscore: private</label>
                    <ul tabIndex={0} className="dropdown-content menu p-2 shadow rounded-box w-52 bg-sky-800">
                        <li className="bg-emerald-500 hover:bg-emerald-300 border-b-[4px] border-sky-800 text-sky-900"><a>public</a></li>
                        <li className="bg-emerald-500 hover:bg-emerald-300 text-sky-900"><a>private</a></li>
                    </ul>
                </div>
            </div>
            <div className="w-1/2 mx-auto">
                <div className="mx-auto dropdown dropdown-right mt-3 mb-6 w-full">
                    <label tabIndex={0} className="btn w-full no-animation m-1 bg-emerald-500 text-sky-900 hover:bg-emerald-300">origin: private</label>
                    <ul tabIndex={0} className="dropdown-content menu p-2 shadow rounded-box w-52 bg-sky-800">
                        <li className="bg-emerald-500 hover:bg-emerald-300 border-b-[4px] border-sky-800 text-sky-900"><a>public</a></li>
                        <li className="bg-emerald-500 hover:bg-emerald-300 text-sky-900"><a>private</a></li>
                    </ul>
                </div>
            </div>
        </div>
    )
}
